var display=document.querySelector(".img-display"),title=document.querySelector(".photo-title"),nxtBtn=document.querySelector(".next-btn"),prevBtn=document.querySelector(".prev-btn"),index=0,images=[{title:"Woman 1",url:"images/woman 1.png"},{title:"Woman 2",url:"images/woman 2.png"},{title:"Woman 3",url:"images/woman 3.png"},{title:"Woman 4",url:"images/woman 4.png"},{title:"Woman 5",url:"images/woman 5.png"},{title:"Woman 6",url:"images/woman 6.png"}];function next(){return index===images.length-1?index=0:index++,index}function prev(){return 0===index?index=images.length-1:index--,index}function displayImage(){var e=images[index].url;display.className.add("change-image"),setTimeout(function(){display.style.backgroundImage="url('"+e+"')"},500),setTimeout(function(){display.classList.remove("change-image")},1e3)}function displayTitle(){var e=images[index].title;title.innerText=" ",setTimeout(function(){title.innerText=e},800)}nxtBtn.addEventListener("click",function(){next(),displayImage(),displayTitle()}),prevBtn.addEventListener("click",function(){prev(),displayImage(),displayTitle()}),displayImage(),displayTitle();